import React, { useState } from 'react';
import LoginPage from './LoginPage.js';

function LoginController(){
    const [identity, setIdentity] = useState({
        username: '',
        password: ''
    });

    function handleUsernameChange(event) {
        const updatedIdentity = {...identity, username:event.target.value};
        setIdentity(updatedIdentity);
    }

    function handlePasswordChange(event) {
        const updatedIdentity = {...identity, password:event.target.value};
        setIdentity(updatedIdentity);
    }

    return(
        <><LoginPage identity={identity} onUsernameChange={handleUsernameChange} onPasswordChange={handlePasswordChange}/></>
    )
};

export default LoginController;
